extends Node

var player_data_resource: PlayerData
var save_file_path: String = ""  # This is only used for saving/loading

func _ready():
    if save_file_path.is_empty():
        printerr("Save file path not set! Cannot load player data.")
        return

    load_player_data()

func set_save_path(path: String):
    save_file_path = path

func set_player_data(resource: PlayerData):
    player_data_resource = resource

func load_player_data():
    if save_file_path.is_empty():
        printerr("Save file path is not set! Cannot load data.")
        return

    if FileAccess.file_exists(save_file_path):
        player_data_resource = load(save_file_path)
    else:
        # Use a default preset, duplicated to avoid modifying the original
        player_data_resource = preload("res://resources/save_data/player_data_preset.tres").duplicate(true)

# Only call this when you explicitly want to save
func save_player_data():
    if save_file_path.is_empty():
        printerr("Save file path is not set! Cannot save data.")
        return

    if player_data_resource:
        ResourceSaver.save(player_data_resource, save_file_path)

