extends "res://addons/gut/test.gd"

func test_addition():
    var result = 2 + 2
    assert_eq(result, 4, "2 + 2 should equal 4")

func test_subtraction():
    var result = 5 - 3
    assert_eq(result, 2, "5 - 3 should equal 2")

func test_mocking():
    var mock = Mock.new()
    mock.expect_call("do_something").and_return(42)

    var result = mock.do_something()
    assert_eq(result, 42, "Mocked function should return 42")

func test_async():
    yield(yield_for(1), YIELD)
    assert_true(true, "Async test passed")

func test_signal():
    var emitter = Node.new()
    emitter.add_user_signal("my_signal")
    var signal_called = false

    emitter.connect("my_signal", self, "_on_my_signal")
    emitter.emit_signal("my_signal")

    func _on_my_signal():
        signal_called = true

    assert_true(signal_called, "Signal should have been called")
