// Non-tail-recursive function (builds up a call stack):
function factorial(n) {
  if (n === 1) return 1;
  return n * factorial(n - 1); // Not tail-recursive: multiplication happens after the recursive call
}

console.log("Factorial (non-TCO):", factorial(5));

// Tail-recursive version (final result passed through accumulator):
function factorialTCO(n, acc = 1) {
  if (n === 1) return acc;
  return factorialTCO(n - 1, n * acc); // Tail position: the recursive call is the last action
}

console.log("Factorial (TCO-style):", factorialTCO(5));
