import React from 'react';

function UserList({ users }) {
  // Check if there are users to render
  if (users.length === 0) {
    return <p>No users found.</p>;
  }

  return (
    <ul>
      {users.map((user, index) => (
        <li key={index}>
          {user.isOnline ? (
            <strong>{user.name} (Online)</strong>
          ) : (
            <span>{user.name} (Offline)</span>
          )}
        </li>
      ))}
    </ul>
  );
}

export default UserList;

import React, { useState, memo } from 'react';

// A pure functional component, using memo to prevent unnecessary re-renders
const PureCounter = memo(({ count }) => {
  console.log('Rendering PureCounter');
  return <p>Count: {count}</p>;
});

// Parent component with a counter state
function CounterApp() {
  const [count, setCount] = useState(0);
  const [message, setMessage] = useState('Hello, React!');

  // Increment count
  const increment = () => setCount(count + 1);

  return (
    <div>
      <h1>{message}</h1>
      <PureCounter count={count} />
      <button onClick={increment}>Increment</button>
    </div>
  );
}

export default CounterApp;

