import React, { useState, useEffect } from 'react';

// A simple component demonstrating the render and commit phases
function RenderCommitExample() {
  // State that will trigger a re-render
  const [count, setCount] = useState(0);

  // useEffect hook to simulate a side effect (commit phase)
  useEffect(() => {
    console.log('Component rendered and committed to the DOM.');
  }, [count]);

  // Event handler to update the state (which triggers the render phase)
  const increment = () => {
    setCount(count + 1);
  };

  return (
    <div>
      <h1>Count: {count}</h1>
      <button onClick={increment}>Increment</button>
    </div>
  );
}

export default RenderCommitExample;

import React, { useState } from 'react';

// Example demonstrating state snapshot and queued updates in React
function StateSnapshotExample() {
  const [count, setCount] = useState(0);
  const [text, setText] = useState('');

  // Event handler for the button click
  const handleClick = () => {
    // Queueing multiple state updates
    setCount(count + 1); // First state update
    setText('Updated'); // Second state update

    // Logs state after multiple updates in the same event handler
    console.log('State after updates:', { count, text });
  };

  return (
    <div>
      <h1>Count: {count}</h1>
      <p>Text: {text}</p>
      <button onClick={handleClick}>Update State</button>
    </div>
  );
}

export default StateSnapshotExample;

import React, { useState } from 'react';

// Example demonstrating updating objects and arrays in React state
function UpdateObjectsArrays() {
  // Object state example
  const [user, setUser] = useState({ name: 'John', age: 30 });

  // Array state example
  const [items, setItems] = useState([1, 2, 3]);

  // Function to update user object state
  const updateUser = () => {
    // Create a new object with updated properties
    setUser((prevUser) => ({ ...prevUser, name: 'Jane' }));
  };

  // Function to add an item to the array
  const addItem = () => {
    // Create a new array with the added item
    setItems((prevItems) => [...prevItems, 4]);
  };

  return (
    <div>
      <h1>User: {user.name}, Age: {user.age}</h1>
      <button onClick={updateUser}>Update User Name</button>
      <h2>Items: {items.join(', ')}</h2>
      <button onClick={addItem}>Add Item</button>
    </div>
  );
}

export default UpdateObjectsArrays;

import React, { useState, useEffect } from 'react';

// Custom hook to fetch data from an API
function useFetch(url) {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(url);
        if (!response.ok) throw new Error('Network response was not ok');
        const result = await response.json();
        setData(result);
      } catch (err) {
        setError(err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [url]);

  return { data, loading, error };
}

// Component using the custom hook
function DataDisplay() {
  const { data, loading, error } = useFetch('https://api.example.com/data');

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;

  return <div>{JSON.stringify(data)}</div>;
}

export default DataDisplay;

